def count_frequency(lst): 
    frequency = {}
    for i in lst: 
        if i in frequency: 
            frequency[i] += 1
        else: 
            frequency[i] = 1

    return frequency

# Driver Code
lst = [2, 3, 4, 5, 6, 4, 3, 5, 7]
